import React, {useEffect, useState} from "react";
import {Fragment} from 'react'
import {Menu, Transition} from '@headlessui/react'
import {SunIcon, MoonIcon} from '@heroicons/react/outline'

function DarkMode() {
  const [appearance, setAppearance] = useState("")

  useEffect(() => {
    if (window.matchMedia('(prefers-color-scheme: dark)').matches) {
      document.documentElement.classList.add('dark')
    } else {
      document.documentElement.classList.remove('dark')
    }
  }, [appearance])  // Save color theme when state change

  function toggleDarkMode() {  // Whenever the user explicitly chooses dark mode
    setAppearance("dark")
    localStorage.theme = 'dark'
  }

  function toggleLightMode() {  // Whenever the user explicitly chooses light mode
    setAppearance("light")
    localStorage.theme = 'light'
  }

  function toggleDefault() {  // Whenever the user explicitly chooses to respect the OS preference
    if (window.matchMedia('(prefers-color-scheme: dark)').matches) {  // system is dark theme
      setAppearance("dark")
    } else {
      setAppearance("light")
    }
    localStorage.removeItem('theme')
  }

  function classNames(...classes) {
    return classes.filter(Boolean).join(' ')
  }

  return (
    <div className="">
      <Menu as="div" className="relative">
        <div>
          <Menu.Button className="flex">
            <span className="sr-only">Open menu</span>
            <SunIcon
              className="w-6 h-6 fill-gray-700 hover:ring-2 hover:ring-gray-700 hover:ring-offset-2 rounded-full"/>
            {appearance === "light" || localStorage.theme === 'light' ? <SunIcon className="h-6 w-6"/> :
              <MoonIcon className="h-6 w-6"/>}
          </Menu.Button>
        </div>

        <Transition
          as={Fragment}
          enter="transition ease-out duration-100"
          enterFrom="transform opacity-0 scale-95"
          enterTo="transform opacity-100 scale-100"
          leave="transition ease-in duration-75"
          leaveFrom="transform opacity-100 scale-100"
          leaveTo="transform opacity-0 scale-95"
        >
          <Menu.Items
            className="origin-top-right absolute right-0 mt-2 w-48 rounded-md shadow-lg py-1 bg-white ring-1 ring-black ring-opacity-5 focus:outline-none">
            <Menu.Item>
              {({active}) => (
                <p
                  className={classNames(active ? 'bg-gray-100 dark:bg-zinc-700' : '', 'block px-4 py-2 text-sm text-gray-700 cursor-pointer dark:text-white')}
                  onClick={toggleLightMode}
                >
                  Light theme
                </p>
              )}
            </Menu.Item>
            <Menu.Item>
              {({active}) => (
                <p
                  className={classNames(active ? 'bg-gray-100 dark:bg-zinc-700' : '', 'block px-4 py-2 text-sm text-gray-700 cursor-pointer dark:text-white')}
                  onClick={toggleDarkMode}
                >
                  Dark theme
                </p>
              )}
            </Menu.Item>
            <Menu.Item>
              {({active}) => (
                <p
                  className={classNames(active ? 'bg-gray-100 dark:bg-zinc-700' : '', 'block px-4 py-2 text-sm text-gray-700 cursor-pointer dark:text-white')}
                  onClick={toggleDefault}
                >
                  Device default
                </p>
              )}
            </Menu.Item>
          </Menu.Items>
        </Transition>
      </Menu>
    </div>
  );
}

export default DarkMode
