import React, { useState} from 'react'
import { BlockPicker } from 'react-color'
import './App.css'

function App() {
  const [color, setColor] = useState("#F9A8D4");
  const [displayColorPicker, setDisplayColorPicker] = useState(false)

  const updateColor=(color) => {setColor(color.hex)}

  return (
    <div className="App">
      <header className="App-header">

        { displayColorPicker ?
          <BlockPicker color={color} onChangeComplete={(color) => updateColor(color)}/>
        : null }
        <p>
          test
        </p>
      </header>
    </div>
  );
}

export default App
