import React, {useRef, useState} from 'react'
import { BlockPicker } from 'react-color'
import './App.css'

function App() {
  const [color, setColor] = useState("#F9A8D4");

  const onChangeComplete=(color) => {setColor(color.hex)}

  return (
    <div className="App">
      <header className="App-header">
        <BlockPicker color={color} onChangeComplete={(color) => onChangeComplete(color)}/>
        <p>
          test
        </p>
      </header>
    </div>
  );
}

export default App
