import React, {useState, Fragment, useEffect} from 'react'
import {TwitterPicker} from 'react-color'
import './App.css'

function App() {
  let locations = [
    {
      name: 'United States',
      areas: [
        {country: 'ðŸ‡ºðŸ‡¸', region: 'N. Virginia', code: 'us-east-1', color: '#023414'},
        {country: 'ðŸ‡ºðŸ‡¸', region: 'Ohio', code: 'us-east-2', color: '#F43254'},
        {country: 'ðŸ‡ºðŸ‡¸', region: 'N. California', code: 'us-west-1', color: '#F43254'},
        {country: 'ðŸ‡ºðŸ‡¸', region: 'Oregon', code: 'us-west-2', color: '#F43254'},
      ],
    },
    {
      name: 'Africa',
      areas: [
        {country: 'ðŸ‡¿ðŸ‡¦', region: 'Cape Town', code: 'af-south-1', color: '#023414', display: false},
      ],
    },
    {
      name: 'Asia Pacific',
      areas: [
        {country: 'ðŸ‡­ðŸ‡°', region: 'Hong Kong', code: 'ap-east-1', color: '#023414', display: false},
        {country: 'ðŸ‡®ðŸ‡©', region: 'Jakarta', code: 'ap-southeast-3', color: '#F43254', display: false},
        {country: 'ðŸ‡®ðŸ‡³', region: 'Mumbai', code: 'ap-south-1', color: '#023414', display: false},
        {country: 'ðŸ‡¯ðŸ‡µ', region: 'Osaka', code: 'ap-northeast-3', color: '#F43254', display: false},
        {country: 'ðŸ‡°ðŸ‡·', region: 'Seoul', code: 'ap-northeast-2', color: '#023414', display: false},
        {country: 'ðŸ‡¸ðŸ‡¬', region: 'Singapore', code: 'ap-northeast-1', color: '#F43254', display: false},
        {country: 'ðŸ‡¦ðŸ‡º', region: 'Sydney', code: 'ap-southeast-2', color: '#023414', display: false},
        {country: 'ðŸ‡¯ðŸ‡µ', region: 'Tokyo', code: 'ap-southeast-1', color: '#F43254', display: false},
      ],
    },
    {
      name: 'Canada',
      areas: [
        {country: 'ðŸ‡¨ðŸ‡¦', region: 'Central', code: 'ca-central-1', color: '#F43254', display: false},
      ],
    },
    {
      name: 'Europe',
      areas: [
        {country: 'ðŸ‡©ðŸ‡ª', region: 'Frankfurt', code: 'eu-central-1', color: '#023414', display: false},
        {country: 'ðŸ‡®ðŸ‡ª', region: 'Ireland', code: 'eu-west-1', color: '#F43254', display: false},
        {country: 'ðŸ‡¬ðŸ‡§', region: 'London', code: 'eu-west-2', color: '#023414', display: false},
        {country: 'ðŸ‡®ðŸ‡¹', region: 'Milan', code: 'eu-south-1', color: '#F43254', display: false},
        {country: 'ðŸ‡«ðŸ‡·', region: 'Paris', code: 'eu-west-3', color: '#023414', display: false},
        {country: 'ðŸ‡¸ðŸ‡ª', region: 'Stockholm', code: 'eu-north-1', color: '#F43254', display: false},
      ],
    },
    {
      name: 'Middle East',
      areas: [
        {country: 'ðŸ‡§ðŸ‡­', region: 'Bahrain', code: 'me-south-1', color: '#023414', display: false},
      ],
    },
    {
      name: 'South America',
      areas: [
        {country: 'ðŸ‡§ðŸ‡·', region: 'SÃ£o Paulo', code: 'sa-east-1', color: '#023414', display: false},
      ],
    }
  ]

  let colorPalettes = {
    "us-east-1": '#023414',
    "us-east-2": '#023414',
    "us-west-1": '#023414',
    "us-west-2": '#023414',
    'af-south-1': '#023414',
    'ap-east-1': '#023414',
    'ap-southeast-3': '#023414',
    'ap-south-1': '#023414',
    'ap-northeast-3': '#023414',
    'ap-northeast-2': '#023414',
    'ap-northeast-1': '#023414',
    'ap-southeast-2': '#023414',
    'ap-southeast-1': '#023414',
    'ca-central-1': '#023414',
    'eu-central-1': '#023414',
    'eu-west-1': '#023414',
    'eu-west-2': '#023414',
    'eu-south-1': '#023414',
    'eu-west-3': '#023414',
    'eu-north-1': '#023414',
    'me-south-1': '#023414',
    'sa-east-1': '#023414'
  }

  let colorPicker = {
    "us-east-1": false,
    "us-east-2": false,
    "us-west-1": false,
    "us-west-2": false,
    'af-south-1': false,
    'ap-east-1': false,
    'ap-southeast-3': false,
    'ap-south-1': false,
    'ap-northeast-3': false,
    'ap-northeast-2': false,
    'ap-northeast-1': false,
    'ap-southeast-2': false,
    'ap-southeast-1': false,
    'ca-central-1': false,
    'eu-central-1': false,
    'eu-west-1': false,
    'eu-west-2': false,
    'eu-south-1': false,
    'eu-west-3': false,
    'eu-north-1': false,
    'me-south-1': false,
    'sa-east-1': false
  }

  const [color, setColor] = useState("#000000")
  const [colorPalette, setColorPalette] = useState(colorPalettes)
  const [displayColorPicker, setDisplayColorPicker] = useState(colorPicker)

  function changeRegionBackgroundColor() {
    const aws_navbar = document.getElementsByClassName("globalNav-032")
    for(let i = 0; i < aws_navbar.length; i++) {
      aws_navbar[i].style.setProperty('background-color', 'red', 'important');
    }
  }

  useEffect(() => {
    if (localStorage.getItem("colorPalette") != null) {
      setColorPalette(JSON.parse(localStorage.getItem("colorPalette")))
    }
  }, [])

  useEffect(() => {
    localStorage.setItem("colorPalette", JSON.stringify(colorPalette))
    console.log("Change saved")
    changeRegionBackgroundColor()
  },);

  const updateColor = (code, color) => {
    setColor(color.hex)
    setColorPalette(previousState => {
      return {...previousState, [code]: color.hex}
    });
  }

  const handleClick = (code) => {  // open popup window
    setDisplayColorPicker(previousState => {
      return {...previousState, [code]: true}
    });
  }

  const handleClose = () => {
    setDisplayColorPicker(false)
  }

  function classNames(...classes) {
    return classes.filter(Boolean).join(' ')
  }

  return (
    <div className="bg-white w-96">
      <header className="">
        <p className="text-xl pl-4 pt-3 font-bold">AWS console region color</p>

        <div className="m-3">
          <div className="px-4">
            <div className="mt-4 flex flex-col">
              <div className="-my-2 -mx-4 overflow-x-auto">
                <div className="inline-block min-w-full py-2 align-middle md:px-6 lg:px-8">
                  <div className="overflow-hidden shadow ring-1 ring-black ring-opacity-5 md:rounded-lg">

                    <table className="min-w-full">
                      <thead className="bg-white">
                      <tr>
                        <th scope="col"
                            className="py-1.5 pl-4 pr-3 text-left text-sm font-semibold text-gray-900 sm:pl-6">
                          Country
                        </th>
                        <th scope="col" className="pl-1 pr-1 py-1.5 text-left text-sm font-semibold text-gray-900">
                          Region
                        </th>
                        <th scope="col" className="pl-1 pr-3 py-1.5 text-left text-sm font-semibold text-gray-900">
                          Code
                        </th>
                        <th scope="col" className="pl-1 pr-3 py-1.5 text-left text-sm font-semibold text-gray-900">
                          Color
                        </th>
                      </tr>
                      </thead>
                      <tbody className="bg-white">

                      {/*location name*/}
                      {locations.map((location) => (
                        <Fragment key={location.name}>
                          <tr className="border-t border-gray-200">
                            <th
                              colSpan={5}
                              scope="colgroup"
                              className="bg-gray-50 px-4 py-1.5 text-left text-sm font-semibold text-gray-900 sm:px-6 text-center"
                            >
                              {location.name}
                            </th>
                          </tr>
                          {/*sub location*/}
                          {location.areas.map((area) => (
                            <tr
                              className={classNames('border-gray-300 border-t')}
                            >
                              <td
                                className="whitespace-nowrap py-1.5 pl-4 pr-3 text-sm font-medium text-gray-900 sm:pl-6">
                                {area.country}
                              </td>
                              <td className="whitespace-nowrap pl-1 pr-3 py-1.5 text-sm text-gray-500">
                                {area.region}
                              </td>
                              <td className="whitespace-nowrap pl-1 pr-3 py-1.5 text-sm text-gray-500">
                                {area.code}
                              </td>
                              <td className="whitespace-nowrap pl-1 pr-3 py-1.5 text-sm text-gray-500">
                                {/*{area.color}*/}
                                <button
                                  type="button"
                                  className="rounded-md border border-transparent bg-indigo-600 px-4 py-2 text-sm font-medium text-white shadow-sm hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 text-center align-middle"
                                  style={{backgroundColor: colorPalette[area.code]}}
                                  onClick={() => {
                                    handleClick(area.code)
                                  }}
                                >
                                </button>
                                {/*ColorPicker popup*/}
                                {displayColorPicker[area.code] ?
                                  <div className="absolute z-[2] right-4 mt-4 mr-2">
                                    <div className="fixed top-0 right-0 left-0 bottom-0" onClick={handleClose}/>
                                    <TwitterPicker color={colorPalette[area.code]}
                                                   onChangeComplete={(color) => updateColor(area.code, color)}
                                                   triangle="top-right"/>
                                  </div>
                                  : null}
                              </td>
                            </tr>
                          ))}
                        </Fragment>
                      ))}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div className="text-center mt-6">
            <p>About</p>
          </div>
        </div>
      </header>
    </div>
  );
}

export default App
