import React, { useState, Fragment} from 'react'
import { BlockPicker } from 'react-color'
import './App.css'

function App() {
  // const [color, setColor] = useState("#F9A8D4");
  // const [displayColorPicker, setDisplayColorPicker] = useState(false)
  //
  // const updateColor=(color) => {
  //   setColor(color.hex)
  // }
  //
  // const handleClick = () => {
  //   setDisplayColorPicker(true)
  // }

  const locations = [
    {
      name: 'us',
      regions: [
        { country: 'ðŸ‡ºðŸ‡¸', region: 'oregon', code: 'us-east-1', color: 'Member' },
        { country: 'ðŸ‡ºðŸ‡¸', region: 'Designer', code: 'us-east-2', color: 'Admin' },
      ],
    },
    // More people...
  ]

  function classNames(...classes) {
    return classes.filter(Boolean).join(' ')
  }

  return (
    <div className="bg-white w-96">
      <header className="">
        <p className="text-xl ml-3 mt-1 text-blue-400 font-bold">AWS console region color</p>
        <div className="m-3">
          <div className="px-4 sm:px-6 lg:px-8">
            <div className="mt-8 flex flex-col">
              <div className="-my-2 -mx-4 overflow-x-auto sm:-mx-6 lg:-mx-8">
                <div className="inline-block min-w-full py-2 align-middle md:px-6 lg:px-8">
                  <div className="overflow-hidden shadow ring-1 ring-black ring-opacity-5 md:rounded-lg">
                    <table className="min-w-full">
                      <thead className="bg-white">
                      <tr>
                        <th scope="col" className="py-1.5 pl-4 pr-3 text-left text-sm font-semibold text-gray-900 sm:pl-6">
                          Country
                        </th>
                        <th scope="col" className="px-3 py-1.5 text-left text-sm font-semibold text-gray-900">
                          Region
                        </th>
                        <th scope="col" className="px-3 py-1.5 text-left text-sm font-semibold text-gray-900">
                          Code
                        </th>
                        <th scope="col" className="px-3 py-1.5 text-left text-sm font-semibold text-gray-900">
                          Color
                        </th>
                        <th scope="col" className="relative py-3.5 pl-3 pr-4 sm:pr-6">
                          <span className="sr-only">Edit</span>
                        </th>
                      </tr>
                      </thead>
                      <tbody className="bg-white">

                      {/*location name*/}
                      {locations.map((location) => (
                        <Fragment key={location.name}>
                          <tr className="border-t border-gray-200">
                            <th
                              colSpan={5}
                              scope="colgroup"
                              className="bg-gray-50 px-4 py-1.5 text-left text-sm font-semibold text-gray-900 sm:px-6"
                            >
                              {location.name}
                            </th>
                          </tr>
                          {/*sub location*/}
                          {location.regions.map((region) => (
                            <tr
                              key={region.country}
                              className={classNames('border-gray-300 border-t')}
                            >
                              <td className="whitespace-nowrap py-4 pl-4 pr-3 text-sm font-medium text-gray-900 sm:pl-6">
                                {region.code}
                              </td>
                              <td className="whitespace-nowrap px-3 py-4 text-sm text-gray-500">{region.color}</td>
                            </tr>
                          ))}
                        </Fragment>
                      ))}
                      </tbody>
                    </table>


                    <div className="sm:flex sm:items-center">
                      <div className="sm:flex-auto">
                        <h1 className="text-xl font-semibold text-gray-900">Users</h1>
                        <p className="mt-2 text-sm text-gray-700">
                          A list of all the users in your account including their name, title, email and role.
                        </p>
                      </div>
                      <div className="mt-4 sm:mt-0 sm:ml-16 sm:flex-none">
                        <button
                          type="button"
                          className="inline-flex items-center justify-center rounded-md border border-transparent bg-indigo-600 px-4 py-2 text-sm font-medium text-white shadow-sm hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 sm:w-auto"
                        >
                          Add user
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </header>
    </div>
  );
}

export default App

