import React, {useState, Fragment, useEffect} from 'react'
import {TwitterPicker} from 'react-color'
import {CogIcon} from '@heroicons/react/solid'
import './App.css'

function App() {
  let locations = [
    {
      name: 'United States',
      areas: [
        {country: 'ðŸ‡ºðŸ‡¸', region: 'N. Virginia', code: 'us-east-1'},
        {country: 'ðŸ‡ºðŸ‡¸', region: 'Ohio', code: 'us-east-2'},
        {country: 'ðŸ‡ºðŸ‡¸', region: 'N. California', code: 'us-west-1'},
        {country: 'ðŸ‡ºðŸ‡¸', region: 'Oregon', code: 'us-west-2'},
      ],
    },
    {
      name: 'Africa',
      areas: [
        {country: 'ðŸ‡¿ðŸ‡¦', region: 'Cape Town', code: 'af-south-1'},
      ],
    },
    {
      name: 'Asia Pacific',
      areas: [
        {country: 'ðŸ‡­ðŸ‡°', region: 'Hong Kong', code: 'ap-east-1'},
        {country: 'ðŸ‡®ðŸ‡©', region: 'Jakarta', code: 'ap-southeast-3'},
        {country: 'ðŸ‡®ðŸ‡³', region: 'Mumbai', code: 'ap-south-1'},
        {country: 'ðŸ‡¯ðŸ‡µ', region: 'Osaka', code: 'ap-northeast-3'},
        {country: 'ðŸ‡°ðŸ‡·', region: 'Seoul', code: 'ap-northeast-2'},
        {country: 'ðŸ‡¸ðŸ‡¬', region: 'Singapore', code: 'ap-southeast-1'},
        {country: 'ðŸ‡¦ðŸ‡º', region: 'Sydney', code: 'ap-southeast-2'},
        {country: 'ðŸ‡¯ðŸ‡µ', region: 'Tokyo', code: 'ap-northeast-1'},
      ],
    },
    {
      name: 'Canada',
      areas: [
        {country: 'ðŸ‡¨ðŸ‡¦', region: 'Central', code: 'ca-central-1'},
      ],
    },
    {
      name: 'Europe',
      areas: [
        {country: 'ðŸ‡©ðŸ‡ª', region: 'Frankfurt', code: 'eu-central-1'},
        {country: 'ðŸ‡®ðŸ‡ª', region: 'Ireland', code: 'eu-west-1'},
        {country: 'ðŸ‡¬ðŸ‡§', region: 'London', code: 'eu-west-2'},
        {country: 'ðŸ‡®ðŸ‡¹', region: 'Milan', code: 'eu-south-1'},
        {country: 'ðŸ‡«ðŸ‡·', region: 'Paris', code: 'eu-west-3'},
        {country: 'ðŸ‡¸ðŸ‡ª', region: 'Stockholm', code: 'eu-north-1'},
      ],
    },
    {
      name: 'Middle East',
      areas: [
        {country: 'ðŸ‡§ðŸ‡­', region: 'Bahrain', code: 'me-south-1'},
      ],
    },
    {
      name: 'South America',
      areas: [
        {country: 'ðŸ‡§ðŸ‡·', region: 'SÃ£o Paulo', code: 'sa-east-1'},
      ],
    }
  ]

  let colorPalettes = {
    "us-east-1": '#1a232f',
    "us-east-2": '#1a232f',
    "us-west-1": '#1a232f',
    "us-west-2": '#1a232f',
    'af-south-1': '#1a232f',
    'ap-east-1': '#1a232f',
    'ap-southeast-3': '#1a232f',
    'ap-south-1': '#1a232f',
    'ap-northeast-3': '#1a232f',
    'ap-northeast-2': '#1a232f',
    'ap-northeast-1': '#1a232f',
    'ap-southeast-2': '#1a232f',
    'ap-southeast-1': '#1a232f',
    'ca-central-1': '#1a232f',
    'eu-central-1': '#1a232f',
    'eu-west-1': '#1a232f',
    'eu-west-2': '#1a232f',
    'eu-south-1': '#1a232f',
    'eu-west-3': '#1a232f',
    'eu-north-1': '#1a232f',
    'me-south-1': '#1a232f',
    'sa-east-1': '#1a232f'
  }

  let colorPicker = {
    "us-east-1": false,
    "us-east-2": false,
    "us-west-1": false,
    "us-west-2": false,
    'af-south-1': false,
    'ap-east-1': false,
    'ap-southeast-3': false,
    'ap-south-1': false,
    'ap-northeast-3': false,
    'ap-northeast-2': false,
    'ap-northeast-1': false,
    'ap-southeast-2': false,
    'ap-southeast-1': false,
    'ca-central-1': false,
    'eu-central-1': false,
    'eu-west-1': false,
    'eu-west-2': false,
    'eu-south-1': false,
    'eu-west-3': false,
    'eu-north-1': false,
    'me-south-1': false,
    'sa-east-1': false
  }

  const [, setColor] = useState("")
  const [colorPalette, setColorPalette] = useState(colorPalettes)
  const [displayColorPicker, setDisplayColorPicker] = useState(colorPicker)

  useEffect(() => {
    // if (localStorage.getItem("colorPalette") != null) {
    //   setColorPalette(JSON.parse(localStorage.getItem("colorPalette")))
    // }

    chrome.storage.sync.get(['colorPalette'], function (result) {
      // console.log('Value currently is ' + JSON.stringify(result["colorPalette"]));
      if (result["colorPalette"] != null) {
        setColorPalette(result["colorPalette"])
      }
    });

  }, [])  // first render

  useEffect(() => {
    // localStorage.setItem("colorPalette", JSON.stringify(colorPalette))

    chrome.storage.sync.set({"colorPalette": colorPalette}, function () {
      console.log("Change saved")
    });
  },);  // every render

  const updateColor = (code, color) => {
    setColor(color.hex)
    setColorPalette(previousState => {
      return {...previousState, [code]: color.hex}
    });
  }

  const handleClick = (code) => {  // open popup window
    setDisplayColorPicker(previousState => {
      return {...previousState, [code]: true}
    });
  }

  const handleClose = () => {
    setDisplayColorPicker(false)
  }

  function classNames(...classes) {
    return classes.filter(Boolean).join(' ')
  }

  // About page
  document.addEventListener('DOMContentLoaded', () => {
    let y = document.getElementById("index_link");
    y.addEventListener("click", openIndex);
  });

  function openIndex() {
    chrome.tabs.create({active: true, url: "https://oranges.dev"});
  }

  return (
    <div className="bg-white w-96">
      <header className="">
        <div className="pl-5 pt-4 flex flex-row">
          <img src="/icon128.png" style={{width: "32px", height: "32px"}} alt="icon"/>
          <p className="text-lg pl-4 pt-1 text-gray-700 font-semibold font-title">Rainbow AWS</p>
          <div className="ml-auto mr-5 mt-2">
            <CogIcon/>
          </div>
        </div>

        <div className="m-3">
          <div className="px-4">
            <div className="mt-4 flex flex-col">
              <div className="-my-2 -mx-4 overflow-x-auto">
                <div className="inline-block min-w-full py-2 align-middle md:px-6 lg:px-8">
                  <div className="overflow-hidden shadow ring-1 ring-black ring-opacity-5 md:rounded-lg">

                    <table className="min-w-full">
                      <thead className="bg-white">
                      <tr>
                        <th scope="col"
                            className="py-1.5 pl-4 pr-3 text-left text-sm font-semibold text-gray-900 sm:pl-6">
                          Country
                        </th>
                        <th scope="col" className="pl-1 pr-1 py-1.5 text-left text-sm font-semibold text-gray-900">
                          Region
                        </th>
                        <th scope="col" className="pl-1 pr-3 py-1.5 text-left text-sm font-semibold text-gray-900">
                          Code
                        </th>
                        <th scope="col" className="pl-1 pr-3 py-1.5 text-left text-sm font-semibold text-gray-900">
                          Color
                        </th>
                      </tr>
                      </thead>
                      <tbody className="bg-white">

                      {/*location name*/}
                      {locations.map((location) => (
                        <Fragment key={location.name}>
                          <tr className="border-t border-gray-200">
                            <th
                              colSpan={5}
                              scope="colgroup"
                              className="bg-gray-50 px-4 py-1.5 text-left text-sm font-semibold text-gray-900 sm:px-6 text-center"
                            >
                              {location.name}
                            </th>
                          </tr>

                          {/*sub location*/}
                          {location.areas.map((area) => (
                            <tr
                              className={classNames('border-gray-300 border-t')}
                            >
                              <td
                                className="whitespace-nowrap py-1.5 pl-4 pr-3 text-sm font-medium text-gray-900 sm:pl-6">
                                {area.country}
                              </td>
                              <td className="whitespace-nowrap pl-1 pr-3 py-1.5 text-sm text-gray-500">
                                {area.region}
                              </td>
                              <td className="whitespace-nowrap pl-1 pr-3 py-1.5 text-sm text-gray-500">
                                {area.code}
                              </td>
                              <td className="whitespace-nowrap pl-1 pr-3 py-1.5 text-sm text-gray-500">

                                <button
                                  type="button"
                                  className="rounded-md border border-transparent bg-indigo-600 px-4 py-2 text-sm font-medium text-white shadow-sm hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-gray-400 focus:ring-offset-2 text-center align-middle"
                                  style={{backgroundColor: colorPalette[area.code]}}
                                  onClick={() => {
                                    handleClick(area.code)
                                  }}
                                >
                                </button>

                                {displayColorPicker[area.code] ?
                                  <div className="absolute z-[2] right-4 mt-4 mr-2">
                                    <div className="fixed top-0 right-0 left-0 bottom-0" onClick={handleClose}/>
                                    <TwitterPicker color={colorPalette[area.code]}
                                                   colors={['#1a232f', '#7B1FA2', '#2E7D32', '#1565C0', '#B71C1C', '#000000', '#0097A7', '#00897B', '#9E9D24', '#880E4F']}
                                                   onChangeComplete={(color) => updateColor(area.code, color)}
                                                   triangle="top-right"/>
                                  </div>
                                  : null}
                              </td>
                            </tr>
                          ))}
                        </Fragment>
                      ))}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div className="text-center mt-6">
            <div id={"index_link"} className="text-blue-700 underline hover:text-blue-900 cursor-pointer">About</div>
          </div>
        </div>
      </header>
    </div>
  );
}

export default App
